<html><head><link rel="stylesheet" href="../gfx/tsview.css" type="text/css" media="screen" /></head><body onload="window.resizeTo(500, 600)"><?phpfunction create_tree($pid, $place, $alldata)	{	foreach($alldata AS $key=>$value)		{		if ($pid==$value['pid'])			{			echo "<div class='channel'>".$place."<div class='chanimg'>&nbsp;</div><div class='channame'>".$value['channel_name']."</div><div class='clear'></div></div>\n";			create_tree($value['cid'], $place."<div class='place'>&nbsp;</div>", $alldata);			}		}	}if(isset($_GET['hostbackup']))		{		$path="backups/channel/hostbackups/";		}		else		{		$path="backups/channel/";		}$handler=file($path."channel_".$_GET['backupid']."_".$_GET['fileport'].".txt");	$getdata=explode('|',$handler[0]);	foreach($getdata AS $key=>$value)		{		$getsettings=explode(' ', $getdata[$key]);		foreach($getsettings AS $key2=>$value2)			{			$equalCount = substr_count($value2, '=');			if($equalCount > 1)				{				$settings = explode('=', $value2);				for($i=2; $i<=$equalCount; $i++) 					{					if(!empty($settings[$i])) 						{						$settings[1].= '='.$settings[$i];						}					else						{						$settings[1].= '=';						}					}				}			else				{				$settings=explode('=', $value2);				}						if(!empty($settings[0]))				{				$backup[$key][$settings[0]]=str_replace('\s', ' ',$settings[1]);				}			}		}create_tree(0, "<div class='place'>&nbsp;</div>" ,$backup)?></body></html>